SELECT NVL(s.username, '(oracle)') AS username,s.osuser,s.sid,s.serial#,p.spid,s.status,s.program,TO_CHAR(s.logon_Time,'DD-MON-YYYY HH24:MI:SS') AS logon_time,s.last_call_et AS last_call_et_secs,s.module,s.action FROM v$session s,v$process p WHERE s.paddr = p.addr AND s.status = 'ACTIVE' ORDER BY s.username, s.osuser
